import React, { useEffect, useState } from 'react';
import Button from './buttonLogin';

import { useNavigate } from 'react-router-dom';
export default function TutorHome() {
    const [masterData, setMasterData] = useState([]);
    const [filterData, setFilterData] = useState([]);
    const [searchValue, setSearchValue] = useState('');
    const navigate = useNavigate();
    useEffect(() => {
        fetch("/data/student.json",
            {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            }).then((response) => response.json())
            .then((results) => {
                //for displaying tutor template change to:
                setMasterData(results.Students);
                setFilterData(results.Students);
            })
            .catch((error) => {
                console.log("error", error);
            })
    }, [])



    const onSearch = (e) => {
        let value = e.target.value;
        const pattern = /^[A-Za-z0-9]+$/;
        if (value === "" || pattern.test(value)) {
            setSearchValue(value);
            filterList(value);
        }
    }
    const filterList = (value) => {
        let filteredValues = masterData.filter(o =>
            Object.keys(o).some(k => `${o[k]}`.toLowerCase().includes(value.toLowerCase())));
        setFilterData(filteredValues)
    }

    return (
        <>
            <h1>Notifications</h1>
            <div className="card" style={{ width: "18rem" }}>
                <div className="card-body">
                    <h5 className="card-title">Amrit Roy</h5>
                    <h6 className="card-subtitle mb-2 text-body-secondary">amritroy@gmail.com</h6>
                    <p className="card-text">Location: Noida</p>
                    <div class="d-flex justify-content-between"><button type="button" class="btn btn-success">Accept</button>
                        <button type="button" class="btn btn-danger">Reject</button></div>

                </div>
            </div>
        </>

    );
}
